CREATE TABLE "ASSIGNMENTS" (
    "ASSIGNMENT_ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999999999999999999999 INCREMENT BY 1 START
    WITH
        1 CACHE 20 NOORDER NOCYCLE NOKEEP NOSCALE NOT NULL ENABLE,
        "ASSIGNMENT_NAME" VARCHAR2 (100 CHAR) NOT NULL ENABLE,
        "DOCUMENT_BLOB" BLOB NOT NULL ENABLE,
        "DOCUMENT_FILENAME" VARCHAR2 (255 CHAR) NOT NULL ENABLE,
        "DOCUMENT_MIMETYPE" VARCHAR2 (100 CHAR) NOT NULL ENABLE,
        "SEMESTER" VARCHAR2 (6 CHAR) NOT NULL ENABLE,
        CONSTRAINT "ASSIGNMENTS_PK" PRIMARY KEY ("ASSIGNMENT_ID") USING INDEX ENABLE
);